drop view if exists area;

create view area as
select row_number() over() as id,pla_id, codi as name, class, geometry from
(
select pla_id,num_rodal as codi,'Rodals' as class, geometry from rodal
where geometry is not null
union
select pla_id,codi,'Xarxa Nova Construció' as class, geometry from xarxa_nova_construccio
where geometry is not null
union
select pla_id,codi,'Xarxa Viaria Existent' as class, st_buffer(geometry, 0.00001) as geometry from xarxa_viaria_existent
where geometry is not null
union
select pla_id,codi,'Lineas Desensa Punts Aigua' as class, st_buffer(geometry, 0.00001) as geometry from lineas_defensa_punts_aigua
where geometry is not null
union
select pla_id,codi,'Canvi Us' as class, geometry from canvi_us
where geometry is not null
) a;

drop view if exists inventari_cd_grup;

create view inventari_cd_grup as
select row_number() OVER () AS id, c.nom as pla,b.num_rodal,a.c_d,sum(a.npeus) as npeus, sum(a_b) as a_b
from inventari_cd a
inner join rodal b
on a.rodal_id  = b.id
inner join pla c
on b.pla_id = c.id
group by c.nom,b.num_rodal ,c_d
order by c.nom, b.num_rodal, a.c_d;

drop view if exists actuacions;

CREATE VIEW public.actuacions
AS
SELECT a.id, b.pla_id,
    a.id as actuacio_id,
    b.num_rodal,
    d.nom AS actuacion,
    a.area_afectada,
    b.geometry,
    any_o_periodicitat
   --string_agg(DISTINCT aa.any_programat::character varying::text, ','::text) AS any_programats
   FROM actuacions_del_pla a
     JOIN rodal b ON a.rodal_id = b.id
     JOIN llistat_actuacions d ON a.actuacio_id = d.id
     JOIN any_actuacio aa ON aa.actuacions_del_pla_id = a.id
  GROUP BY b.pla_id,a.id, b.num_rodal, d.nom, a.area_afectada, b.geometry, any_o_periodicitat
  ORDER BY b.num_rodal, d.nom;
