INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(1,'Bpe01', 'Qualitat d’estació alta. Estructura regular. Torn de tallada de 45-65 anys (Dg 30-35 cm). Règim d’aclarides selectives. Densitat final de 300-400 peus/ha. Regeneració per tallada arreu en dos temps.',17);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(2,'Bpe02', 'Qualitat d’estació alta. Estructura irregular amb 2 estrats (bosc mitjà irregular). Un estrat inferior regular de rebrot amb destí a llenyes i un estrat superior irregular, principalment de llavor, de bedoll i altres espècies. Rotació de 25-35 anys (Dg ~15-20 cm per a l’estrat de rebrot). Regeneració per tallada arreu amb reserva de peus de llavor de bedoll o d’altres espècies.',17);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(3,'Bpe03', 'Qualitat d’estació baixa. Estructura irregular amb 2 estrats (bosc mitjà irregular). Un estrat inferior regular de rebrot i un estrat superior irregular, principalment de llavor, de bedoll i altres espècies. Rotació de 25 a 35 anys (Dg ~15 cm per a l’estrat de rebrot). Regeneració per tallada arreu amb reserva de peus.',17);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(4,'Cs01', 'Qualitat d’estació alta. Estructura regular. Producció de fusta de serra amb Ø~30 cm. Massa originada per nova plantació. Torn de tallada al voltant de 40 anys (Dg ~30 cm). Regeneració per tallada final arreu. Opció de gestió amb bosc alt.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(5,'Cs02', 'Qualitat d’estació alta. Estructura regular. Producció de fusta de serra amb Ø~25 cm. Torn de tallada al voltant de 35 anys (Dg ~25 cm). Selecció de tanys tardana i aclarida selectiva. Densitat final ~1 tany/soca. Regeneració per tallada final arreu. Opció de reservar peus per aconseguir un Dg ~30 cm.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(6,'Cs03', 'Qualitat d’estació alta. Estructura regular. Producció de fusta de serra amb Ø~22 cm. Torn de tallada al voltant de 30 anys (Dg ~22 cm). Reducció de densitat en dues intervencions: selecció de tanys tardana i aclarida baixa. Densitat final ~1 tany/soca. Regeneració per tallada final arreu.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(7,'Cs04', 'Qualitat d’estació alta. Estructura regular. Producció de barramenta.Torn de tallada al voltant de 20 anys (Dg >15 cm). Una única intervenció de millora. Densitat final ~3 tanys/soca. Regeneració per tallada final arreu.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(8,'Cs05', 'Qualitat d’estació mitjana. Estructura regular. Producció de barramenta. Torn de tallada sobre els 28 anys (Dg >14 cm). Una única intervenció de millora. Densitat final ~3 tanys/soca. Regeneració per tallada final arreu.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(9,'Cs06', 'Qualitat d’estació alta, mitjana o baixa. Estructura regular. Producció de fruit. Torn de tallada físic i regeneració per plantació. Pràctiques culturals anuals.',20);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(10,'Fs01', 'Qualitat d’estació alta. Estructura regular. Fusta de qualitat amb diàmetre final ~50 cm. Torn de tallada de 100-120 anys (Dg ~50 cm). Règim d’aclarides selectives mixtes cada 15-20 anys. Densitat final de 340 peus/ha. Regeneració per aclarida successiva en tres fases.',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(11,'Fs02', 'Qualitat d’estació alta. Estructura irregular per bosquets petits. Diàmetre màxim de 60 cm. Tallades de selecció cada 10-12 anys, amb obertures orientativament de 400 a 1.200 m2. Es manté més presència d’arbres dels grups de grandària mitjà i gran i menor del grup petit (CD 10-15: 25%% AB, CD 20-30: 35%% AB, CD =35: 40%% AB). L’àrea basal extreta màxima és del 30%%.',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(12,'Fs03', 'Qualitat d’estació mitjana. Estructura regular. Torn de tallada de 100-120 anys (Dg ~40 cm). Règim d’aclarides baixes cada 15-20 anys. Densitat final de 325 peus/ha. Regeneració per aclarida successiva en tres fases.',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(13,'Fs04', 'Qualitat d’estació mitjana. Estructura irregular per bosquets petits. Diàmetre màxim de 45 cm. Tallades de selecció cada 15-17 anys, amb obertures orientativament de 400 a 1.200 m2, i es manté més presència d’arbres del grup de grandària mitjà (CD 10-15: 30%% AB, CD 20-30: 40%% AB, CD =35: 30%% AB). L’àrea basal extreta màxima és del 33%%.',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(14,'Fs05', 'Qualitat d’estació baixa. Estructura regular. Torn de tallada de 110-135 anys (Dg ~30 cm). Règim d’aclarides baixes cada 20-25 anys. Densitat final de 595 peus/ha. Regeneració per aclarida successiva en tres fases.',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(15,'Fs06', 'Qualitat d’estació baixa. Estructura irregular per bosquets petits. Diàmetre màxim de 40 cm. Tallades de selecció cada 20-22 anys, amb obertures orientativament de 400 a 1.200 m2, i es manté més presència d’arbres dels grups de grandària mitjà i petit (CD 10-15: 35%% AB, CD 20-30: 42%% AB, CD =35: 23%% AB). L’àrea basal extreta màxima és del',16);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(16,'Fe01', 'Qualitat d’estació alta. Estructura regular. Silvicultura d’arbre individual. Torn de tallada al voltant dels 60 anys (no superior) anys (Dn dels arbres objectiu >50 cm). Règim d’aclarides selectives estrictes o mixtes en cas de fàcil accessibilitat. Densitat final de 80 peus/ha.',18);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(17,'Fe02', 'Qualitat d’estació alta. Estructura irregularitzada amb 2 estrats (bosc mitjà irregular). Un estrat inferior regular de rebrot amb destí a llenyes i un estrat superior irregular, principalment de llavor, de freixe i altres espècies. Rotació de 20-30 anys (Dg ~15-20 cm per a l’estrat de rebrot). Regeneració per tallada arreu amb reserva de peus de llavor de freixe o d’altres espècies.',18);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(18,'Fe03', 'Qualitat d’estació baixa. Estructura regular amb un estrat a bosc menut o irregularitzada amb 2 estrats (bosc mitjà irregular), un estrat inferior regular de rebrot amb destí a llenyes i un estrat superior irregular, principalment de llavor, de freixe i altres espècies. Rotació de 25-35 anys (Dg =15 cm per a l’estrat de rebrot). Regeneració per tallada arreu amb eventual reserva de peus de llavor de freixe o d’altres espècies.',18);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(19,'Qca01', 'Qualitat d’estació alta. Alt risc d’incendi. Estructura regular amb tractaments de millora (aclarides). Torn de tallada a partir de 120 anys, Dn= 50 cm. Inclou tractaments de creació de discontinuïtat vertical (estassades, podes).',13);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(20,'Qca02', 'Qualitat d’estació alta. Estructura irregular. Diàmetre màxim de 60 cm. Tallades de selecció cada 20 anys, per claps. L’àrea basal extreta és del 40%%.',13);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(21,'Qca03', 'Qualitat d’estació baixa. Alt risc d’incendi. Estructura regular amb tractaments de millora (aclarides). Torn de tallada a partir de 100 anys, Dn= 40 cm. Inclou tractaments de creació de discontinuïtat vertical (estassades, podes).',13);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(22,'Qf01', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular. Producció de llenyes. Torn mínim de tallada al voltant de 50 anys (Dg ~17 cm). Amb un tractament intermedi de millora (selecció de tanys). Densitat final de 1.200 peus/ha. Tallada de regeneració arreu o en dos temps.',11);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(23,'Qf02', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Augment de la resistència al foc i producció a llarg termini. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. En zona d’alt risc d’incendi inclou tractaments de creació de discontinuïtat vertical (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',11);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(24,'Qf03', 'Qualitat d’estació baixa. Baix i alt risc d’incendi. Augment de la resistència al foc i producció a llarg termini. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada a partir dels 70 anys i fins als 100 anys. En zona d’alt risc d’incendi inclou tractaments de creació de discontinuïtat vertical (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',11);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(25,'Qf04', 'Qualitat d’estació alta o baixa. Gestió estructural amb objectiu de producció de llenyes, prevenció d’incendis i ús de pastures. Estructura regularitzada amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. Inclou tractaments de creació de discontinuïtat vertical i millora de l’accessibilitat a pastura (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',11);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(26,'Qh1', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular. Producció de llenyes. Torn mínim de tallada al voltant dels 35-40 anys (Dg ~18 cm). Amb un tractament intermedi de millora (selecció de tanys). Densitat final de 1.000 peus/ha. Tallada de regeneració arreu o en dos temps.',7);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(27,'Qh2', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Augment de la resistència al foc i producció a llarg termini. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. En zona d’alt risc d’incendi inclou tractaments de creació de discontinuïtat vertical (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',7);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(28,'Qh3', 'Qualitat d’estació alta. Baix risc d’incendi. Estructura semiregular per colps o bosquets petits. Producció de llenyes amb tallades de selecció. Distribució de referència: Dmax: 20 cm; N: 500 peus/ha; AB: ~7-8 m2/ha; Fcc: 30-40%%. Tallada de selecció per colps o bosquets petits cada 25-30 anys. L’àrea basal extreta pot superar el 70%%.',7);
