INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(31,'Qh6', 'Qualitat d’estació baixa. Baix risc d’incendi. Estructura semiregular per colps o bosquets petits. Producció de llenyes amb tallades de selecció. Distribució de referència: Dmax: 20 cm; N: 500 peus/ha; AB: ~7-8 m2/ha; Fcc: 30-40%%. Tallada de selecció per colps o bosquets petits cada 40 anys. L’àrea basal extreta pot superar el 70%%.',7);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(32,'Qh7', 'Qualitat d’estació alta o baixa. Gestió estructural amb objectiu de producció de llenyes, prevenció d’incendis i ús de pastures. Estructura regularitzada amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. Inclou tractaments de creació de discontinuïtat vertical i millora de l’accessibilitat a pastura (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',7);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(33,'Qib01', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular. Torn de tallada al voltant de 40 anys (~16 cm). Amb un tractament intermedi de millora (selecció de tanys). Densitat final de 1.500 peus/ha. Tallada de regeneració arreu o en dos temps.',9);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(34,'Qib02', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. Inclou tractaments de creació de discontinuïtat vertical (estassades, podes).',9);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(35,'Qib03', 'Qualitat d’estació baixa. Baix i alt risc d’incendi. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. Inclou tractaments de creació de discontinuïtat vertical (estassades, podes).',9);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(36,'Qii01', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura irregular peu a peu. Distribució de referència: Dmax: 30 cm;  N: 955 peus/ha; AB: 15,3 m2/ha; Fcc: 50-60 %%. Tallada de selecció peu a peu cada 15-20 anys. L’àrea basal extreta és del 40-50%%.',8);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(37,'Qii02', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular. Torn de tallada al voltant de 35 anys (Dg ~18 cm). Amb un tractament intermedi de millora (selecció de tanys). Densitat final de 1.300 peus/ha. Tallada de regeneració arreu o en dos temps.',8);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(38,'Qii03', 'Qualitat d’estació alta. Baix i alt risc d’incendi. Estructura regular amb tractaments de millora (selecció de tanys i aclarides). Torn de tallada al voltant de 100 anys. En zona d’alt risc d’incendi inclou tractaments de creació de discontinuïtat vertical (estassades, podes). En zona de baix risc d’incendi únicament inclou tractaments de regulació de la densitat (selecció de tanys i aclarides).',8);
