INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(94,'Ppa10', 'Qualitat d’estació baixa. Augment de la resistència al foc. Estructura regularitzada amb torn de tallada de 170-200 anys. Almenys una aclarida baixa als 75 anys, amb actuacions per reduir la vulnerabilitat estructural. Densitat final de 450 peus/ha. Regeneració per aclarida successiva en tres fases.',6);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(95,'Ps01', 'Qualitat d’estació alta i baix risc d’incendi. Estructura regularitzada. Fusta de qualitat amb diàmetre final ~50 cm. Torn de tallada de 90-110 anys. Règim d’aclarides selectives cada 10-15 anys. Podes en alçada junt amb les primeres aclarides. Densitat final de 200 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(96,'Ps02', 'Qualitat d’estació alta i baix risc d’incendi. Estructura regularitzada. Fusta de qualitat amb diàmetre final ~50 cm. Torn de tallada de 105-130 anys. Règim d’aclarides baixes cada 10-20 anys. Densitat final de 250 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(97,'Ps03', 'Qualitat d’estació alta i baix risc d’incendi. Estructura regularitzada. Fusta de qualitat amb diàmetre final ~35 cm. Torn de tallada de 70-85 anys. Règim d’aclarides baixes cada 11-15 anys. Densitat final de 600 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(98,'Ps04', 'Qualitat d’estació alta i baix risc d’incendi. Estructura irregularitzada. Fusta de qualitat amb diàmetre màxim ~55 cm. Tallades de selecció cada 7-13 anys, amb obertures de 1.000-3.000 m2, mantenint major presència d’arbres del grup de grandària gran (CD 10-15: 27%% AB, CD 20-30: 31%% AB, CD=35: 42%% AB). L’àrea basal extreta màxima és del 35-40%%.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(99,'Ps05', 'Qualitat d’estació alta i baix risc d’incendi. Estructura irregularitzada. Fusta de qualitat amb diàmetre màxim ~35 cm. Tallada de selecció cada 7-13 anys, amb obertures de 1.000-3.000 m2, mantenint major presència d’arbres del grup de grandària petit i molt menor del grup gran (CD 10-15: 46%% AB, CD 20-30: 46%% AB, CD=35: 8%% AB). L’àrea basal extreta màxima és del 40-42%%.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(100,'Ps06', 'Qualitat d’estació alta/mitjana/baixa i alt/baix risc d’incendi. Estructura irregularitzada. Fusta de qualitat/comercial. Presència mínima de tres classes d’edat o estat de desenvolupament en bosquets de 5.000-10.000 m2. Gestió dels bosquets com a massa regularitzada amb regeneració per aclarida successiva.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(101,'Ps07', 'Qualitat d’estació alta i alt risc d’incendi. Estructura regularitzada. Fusta de qualitat amb diàmetre final ~50 cm. Torn de tallada de 115-140 anys. Règim d’aclarides baixes cada 12-25 anys. Tractaments complementaris per a reduir la vulnerabilitat estructural (podes, estassades). Densitat final de 350 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(102,'Ps08', 'Qualitat d’estació mitjana i baix risc d’incendi. Estructura regularitzada. Fusta de qualitat amb diàmetre final ~40 cm. Torn de tallada de 115-140 anys. Règim d’aclarides selectives cada 17-29 anys. Densitat final de 300 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(103,'Ps09', 'Qualitat d’estació mitjana i baix risc d’incendi. Estructura regularitzada. Fusta comercial amb diàmetre final ~35 cm. Torn de tallada de 100-125 anys. Règim d’aclarides baixes cada 15-27 anys. Densitat final de 600 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(104,'Ps10', 'Qualitat d’estació mitjana i baix risc d’incendi. Estructura irregularitzada. Fusta comercial amb diàmetre màxim ~45 cm. Diàmetre màxim de 45 cm. Tallada de selecció cada 13-18 anys, amb obertures de 1.000-3.000 m2, mantenint major presència d’arbres del grup de grandària mitjà (CD 10-15: 33%% AB, CD 20-30: 37%% AB, CD=35: 30%% AB). L’àrea basal extreta màxima és del 35-40%%.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(105,'Ps11', 'Qualitat d’estació mitjana i alt risc d’incendi. Estructura regularitzada. Fusta comercial amb diàmetre final ~40 cm. Torn de tallada de 120-150 anys. Règim d’aclarides baixes cada 11-24 anys. Tractaments complementaris per a reduir la vulnerabilitat estructural (podes, estassades). Densitat final de 400 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(106,'Ps12', 'Qualitat d’estació mitjana i alt risc d’incendi. Estructura regularitzada. Torn de tallada físic. Tractaments per a reduir la vulnerabilitat estructural (aclarides, podes, estassades). Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(107,'Ps13', 'Qualitat d’estació baixa i baix risc d’incendi. Estructura regularitzada. Torn de tallada de 130-160 anys (~30 cm). Règim d’aclarides selectives cada 17-25 anys. Densitat final de 450 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(108,'Ps14', 'Qualitat d’estació baixa i baix risc d’incendi. Estructura irregularitzada. Diàmetre màxim de 35 cm. Tallada de selecció cada 19-24 anys, amb obertures de 1.000-3.000 m2, mantenint menor presència d’arbres del grup de grandària gran (CD 10-15: 44%% AB, CD 20-30: 49%% AB, CD=35: 7%% AB). L’àrea basal extreta màxima és del 35%%.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(109,'Ps15', 'Qualitat d’estació baixa i alt risc d’incendi. Estructura regularitzada. Torn de tallada de 140-170 anys (~30 cm). Règim d’aclarides baixes cada 11-27 anys. Tractaments complementaris per a reduir la vulnerabilitat estructural (podes, estassades). Densitat final de 500 peus/ha. Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(110,'Ps16', 'Qualitat d’estació baixa i alt risc d’incendi. Estructura regularitzada. Torn de tallada físic. Tractaments per a reduir la vulnerabilitat estructural (aclarides, podes, estassades). Regeneració per aclarida successiva en tres fases.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(111,'Ps17', 'Qualitat d’estació mitjana i baix risc d’incendi. Estructura regularitzada. Producció de bolets i fusta de grans dimensions. Règim d’aclarides mixtes.',3);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(112,'Pu01', 'Qualitat d’estació alta. Producció de fusta. Estructura regularitzada amb torn de tallada de 120-150 anys (~45 cm). Règim d’aclarides selectives mixtes cada 15-20 anys. Densitat final de 400-600 peus/ha. Regeneració per aclarida successiva en dues fases.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(113,'Pu02', 'Qualitat d’estació alta. Producció de fusta. Estructura regularitzada amb torn de tallada de 120-150 anys (~45 cm). Règim d’aclarides selectives cada 15-25 anys. Densitat final de ~400 peus/ha. Regeneració per aclarida successiva en tres fases.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(114,'Pu03', 'Qualitat d’estació alta. Producció de fusta. Estructura irregularitzada. Diàmetre màxim de 45 cm. Tallades de selecció cada 10-15 anys, amb obertures de fins a 3.000 m2. Estructura equilibrada per grups de grandària. L’àrea basal extreta màxima és del 35%%.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(115,'Pu04', 'Qualitat d’estació alta/mitjana/baixa. Producció de fusta. Estructura irregularitzada. Presència mínima de tres classes d’edat o estat de desenvolupament en bosquets de 5.000 a 10.000 m2. Gestió dels bosquets com a massa regularitzada amb regeneració per aclarida successiva.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(116,'Pu05', 'Qualitat d’estació mitjana. Producció de fusta. Estructura regularitzada amb torn de tallada de 120-150 anys (~40 cm). Règim d’aclarides selectives mixtes cada 20-30 anys. Densitat final de ~500 peus/ha. Regeneració per aclarida successiva en dues fases.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(117,'Pu06', 'Qualitat d’estació mitjana. Producció de fusta. Estructura irregularitzada. Diàmetre màxim de 40 cm. Tallades de selecció cada 15-20 anys, amb obertures de fins a 2.500 m2. Estructura equilibrada per grups de grandària. L’àrea basal extreta màxima és del 35%%.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(118,'Pu07', 'Qualitat d’estació baixa. Producció de fusta. Estructura regularitzada amb torn de tallada de 150-200 anys (~35 cm). Règim d’aclarides selectives cada 20-30 anys. Densitat final de ~750 peus/ha. Regeneració per aclarida successiva.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(119,'Pu08', 'Qualitat d’estació baixa. Producció de fusta. Estructura irregularitzada. Diàmetre màxim de 35 cm. Tallades de selecció cada 20-30 anys, amb obertures de fins a 2.000 m2. Estructura amb menor presència del grup de grandària gran. L’àrea basal extreta màxima és del 40%%.',2);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(120,'Pt01', 'Qualsevol qualitat d’estació. Estructura regular. Aclarides selectives estrictes o mixtes en cas de fàcil accés. Diàmetre final mitjà: 40 cm (torn ~50 anys). Densitat final 200-250 peus/ha',31);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(121,'Pt02', 'Qualsevol qualitat d’estació. Estructura irregularitzada amb 2 estrats (bosc mitjà irregular). Un estrat inferior regular majoritàriament de rebrot de trèmol amb destí a trituració o llenyes i un estrat superior irregular de trèmol i altres espècies. Rotació de 20-30 anys (Dg ~15-20 cm per a l’estrat de rebrot). Regeneració per tallada arreu amb reserva de peus de trèmol o d’altres espècies.',31);
INSERT INTO orgest (id,acronym, descripcio, especie_principal_id) VALUES(122,'Ce01', 'Qualitat d’estació òptima. Fusta d’alta qualitat amb diàmetre final >45 cm. Aclarides selectives o mixtes i podes de qualitat (Densitat inicial 800-1200 peus/ha. 2-3 aclarides selectives o mixtes. Podes (opcionals en qualitats subòptimes). Ø final=45 cm. Nfinal=250-300, T= 70 anys).',40);
